mapper.select:
select e.id as id, s.small_sb_no as smallNo, b.mark_number as betonNo, e.time as time, e.temperature as temperature, e.norm as norm, e.operator as operator, e.bl as bl from inbound_temp_info e left join pouring_base p on e.pouring_base_id=p.id left join small_storage_bin s on p.small_sb_id = s.id left join beton b on p.beton_id = b.id
and e.time >=#{startTime}
and e.time <= #{endTime}
and e.temperature >=#{valStart}
and e.temperature <= #{valEnd}
and s.id = #{smallId}
order by e.time desc

SELECT s.small_sb_no as smallNo, avg(e.temperature) as avgTemp, max(e.temperature)as topTemp, min(e.temperature)as bottomTemp, max(e.norm) as normTemp, count(e.id) as recordCount, sum(e.temperature>e.norm) as excessiveCount from inbound_temp_info e left join pouring_base p on e.pouring_base_id=p.id left join small_storage_bin s on p.small_sb_id = s.id
and s.id = #{sbId}
GROUP BY smallNo
