mapper.select:
select wpi.id as id, s.small_sb_no as smallNo, wpi.time as time, wp.wp_no as waterPipeNo, wpi.enter_temp as enterTemp, wpi.out_temp as outTemp, wpi.enter_mpa as enterMpa, wpi.out_mpa as outMpa, wpi.flow as flow, wpi.opening as opening, wpi.bl as bl, wpi.qi as qi from water_pipe_flow_info wpi left join water_pipe wp on wpi.water_pipe_id= wp.id left join small_storage_bin s on wp.sb_id = s.id
and wpi.time >=#{startTime}
and wpi.time <= #{endTime}
and s.id=#{smallId}
and wpi.water_pipe_id = #{waterPipeId}
order by wpi.time desc

SELECT tb1.*, tma.avg_temp as avgTempMeasurements, tb2.avgInnerTemp FROM ( SELECT LEFT (wpfi.time, 10) AS time, avg(wpfi.enter_temp) AS avgEnterTemp, AVG(wpfi.out_temp) AS avgOutTemp, AVG(wpfi.flow) AS avgFlow FROM water_pipe_flow_info wpfi LEFT JOIN water_pipe wp ON wpfi.water_pipe_id = wp.id
and wp.sb_id = #{sbId}
and time >=#{startTime}
and time <= #{endTime}
GROUP BY LEFT (wpfi.time, 10) ) tb1 LEFT JOIN temp_measurements_assess tma ON tma.time = tb1.time LEFT JOIN ( SELECT LEFT (itsi.time, 10) AS time, avg(itsi.temp) AS avgInnerTemp FROM inner_temp_sensor_info itsi LEFT JOIN inner_temp_sensor its ON its.id = itsi.inner_temp_sensor_id
and its.sb_id = #{sbId}
GROUP BY LEFT (itsi.time, 10) ) tb2 ON tb1.time = tb2.time ORDER BY tb1.time
